CREATE TABLE IF NOT EXISTS car_information (
    `id` INT PRIMARY KEY AUTO_INCREMENT CHARACTER SET utf8,
    `make` VARCHAR(255) CHARACTER SET utf8,
    `model` VARCHAR(255) CHARACTER SET utf8,
    `model_platform_years` VARCHAR(255) CHARACTER SET utf8,
    `model_version` VARCHAR(255) CHARACTER SET utf8,
    `body_type` VARCHAR(255) CHARACTER SET utf8,
    `doors` VARCHAR(255) CHARACTER SET utf8,
    `tire_size` VARCHAR(255) CHARACTER SET utf8,
    `tire_size_rear` VARCHAR(255) CHARACTER SET utf8,
    `engine_type` VARCHAR(255) CHARACTER SET utf8
);
